import{_ as a,c as e}from"./app.1374eb62.js";const r={},p=e('<h1 id="\u79BB\u5FC3\u5F0F\u6D17\u53F7\u2014\u2014\u63ED\u79D8\u5C81\u57CE\u79BB\u5FC3\u4E0D\u4E3A\u4EBA\u77E5\u7684\u4E8B\u5B9E" tabindex="-1"><a class="header-anchor" href="#\u79BB\u5FC3\u5F0F\u6D17\u53F7\u2014\u2014\u63ED\u79D8\u5C81\u57CE\u79BB\u5FC3\u4E0D\u4E3A\u4EBA\u77E5\u7684\u4E8B\u5B9E" aria-hidden="true">#</a> \u79BB\u5FC3\u5F0F\u6D17\u53F7\u2014\u2014\u63ED\u79D8\u5C81\u57CE\u79BB\u5FC3\u4E0D\u4E3A\u4EBA\u77E5\u7684\u4E8B\u5B9E</h1><p>\u5C81\u57CE\u79BB\u5FC3\uFF0C\u771F\u540D\uFF1A\u8C22\u555F\u946B\uFF0C\u76EE\u524D\u662F QQ\u7FA4\uFF1A337971357 \u7FA4\u4E3B\uFF0C\u6597\u9C7C\u76F4\u64AD\u95F4\u53F7\u4E3A\uFF1A<a href="https://www.douyu.com/875191" target="_blank" rel="noopener noreferrer">875191</a>\uFF0C\u76EE\u524D\u4F7F\u7528\u7684 QQ \u53F7\u4E3A\uFF1A544055641\uFF0C\u5DF2\u77E5\u4F7F\u7528\u4E24\u4E2A\u652F\u4ED8\u5B9D\u3002</p><p>\u672C\u6587\u4F1A\u6709\u5C11\u91CF\u4E2A\u4EBA\u70B9\u8BC4\uFF0C\u4F46\u7EAF\u5C5E\u64B0\u5199\u771F\u5B9E\u4E8B\u4EF6\uFF0C\u63ED\u79D8\u5C81\u57CE\u79BB\u5FC3\u4E0D\u4E3A\u4EBA\u77E5\u7684\u4E8B\u5B9E\u3002</p><h2 id="\u4E00\u3001\u8D77\u6E90" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001\u8D77\u6E90" aria-hidden="true">#</a> \u4E00\u3001\u8D77\u6E90</h2><p>\u4ECE\u56FD\u670D\u521A\u521A\u6765\u5230\u53F0\u670D\u7684\u79BB\u5FC3\uFF0C\u540C\u4E0D\u5C11\u7684\u840C\u65B0\u90FD\u4E00\u6837\uFF0C\u4EC0\u4E48\u90FD\u4E0D\u61C2\u3002</p><p>\u4E00\u5207\u7684\u5F00\u7AEF\uFF0C\u5C31\u4ECE\u79BB\u5FC3\u79DF\u8F6E\u65F6\uFF0C\u9047\u5230\u4E86\u67EF\u5357\u3002</p><p>\u90A3\u65F6\u7684\u79BB\u5FC3\uFF0C\u5BF9\u88C5\u5907\u4E0D\u4E86\u89E3\uFF0C\u5BF9\u804C\u4E1A\u4E0D\u4E86\u89E3\uFF0C\u5BF9\u7EC3\u7EA7\u65B9\u6CD5\u4E5F\u540C\u6837\u4E0D\u4E86\u89E3\uFF01</p><p>\u597D\u5FC3\u7684\u67EF\u5357\uFF0C\u5E26\u7740 link \u5957\u548C\u82B1\u8F6E\uFF0C\u4E00\u628A\u9F3B\u6D95\u4E00\u628A\u5C3F\u6559\u79BB\u5FC3\u600E\u6837\u5FEB\u901F\u7EC3\u7EA7\uFF0C\u8FD8\u624B\u628A\u624B\u6559\u5BFC\u4E86\u5F88\u591A\u67AB\u4E4B\u8C37\u8FD9\u6E38\u620F\u7684\u76F8\u5173\u77E5\u8BC6\u70B9\u3002</p><p>\u4E0D\u53EF\u7F6E\u5426\uFF0C\u67EF\u5357\u662F\u79BB\u5FC3\u7684\u67AB\u4E4B\u8C37\u5BFC\u5E08\u3002</p><h2 id="\u4E8C\u3001\u67EF\u5357\u5E26\u7740\u79BB\u5FC3\u8D70\u8FDB\u4EE3\u7EC3" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u3001\u67EF\u5357\u5E26\u7740\u79BB\u5FC3\u8D70\u8FDB\u4EE3\u7EC3" aria-hidden="true">#</a> \u4E8C\u3001\u67EF\u5357\u5E26\u7740\u79BB\u5FC3\u8D70\u8FDB\u4EE3\u7EC3</h2><p>\u5728 2019\u5E74\u5E74\u4E2D\uFF0C\u67EF\u5357\u540C\u79BB\u5FC3\u53D1\u8D77\u4E86\u63A5\u70B9\u4EE3\u7EC3\u6765\u505A\uFF0C\u4EE5\u8D5A\u70B9\u5916\u5FEB\u3002\u4ED6\u4EEC\u4E24\u4EBA\u4E00\u8D77\u51FA\u94B1\u4E70\u4E86\u4E00\u4E2A\u71C3\u70E7\u4E4B\u6212\u3002</p><p>\u5F53\u65F6\u8BF4\u597D\u4E86\u662F 7\uFF1A3 \u5206\uFF0C\u79BB\u5FC3\u627E\u5BA2\u6237\u62FF 3\u6210\uFF0C\u67EF\u5357\u6253\u53F7\u62FF 7\u6210\uFF08\u90A3\u65F6\u79BB\u5FC3\u4F9D\u65E7\u8FD8\u662F\u4E0D\u4F1A\u9AD8\u6548\u7EC3\u7EA7\uFF0C\u4F46\u662F\u5DF2\u7ECF\u4E0D\u5C5E\u4E8E\u840C\u65B0\u4E86\uFF09\u3002\u79BB\u5FC3\u62FF 3\u6210\uFF0C\u662F\u8981\u8D1F\u8D23\u6302\u82B1\u8F6E\u7684\u3002</p><p>\u57FA\u672C\u4E0A\u662F\u76F4\u64AD\u6253\u53F7\uFF0C\u6253\u4E86\u6CA1\u51E0\u4E2A\u5355\u5B50\u4E4B\u540E\uFF0C\u5C31\u53D1\u8D77\u4E86\u5EFA\u7ACB\u4E00\u4E2A QQ\u4EA4\u6D41\u7FA4\uFF08\u73B0\u5728\u79BB\u5FC3\u7684\u4EA4\u6613\u7FA4\u524D\u8EAB\uFF09\uFF0C\u9080\u8BF7\u4E86\u5F53\u65F6\u6240\u6709\u6597\u9C7C\u76F4\u64AD\u7684\u4E3B\u64AD\u3002</p><p>\u5982\uFF1A\u597D\u5947\uFF0C\u767E\u5DDD\uFF0C\u9ED1\u7AE5\u8BDD\u7B49\u7B49\uFF0C\u5F53\u7136\u4E5F\u8FD8\u6709\u6211\u5728\u5185\u3002</p><p>\u4E00\u76F4\u6301\u7EED\u4E86\u4E24\u4E2A\u6708\u4E4B\u540E\uFF0C\u67EF\u5357\u5C31\u56DE\u53BB\u4E0A\u73ED\u4E86\u3002\u7531\u4E8E\u4E0A\u73ED\u4E4B\u540E\uFF0C\u53EA\u80FD\u665A\u4E0A\u6253\u6253\u53F7\uFF0C\u6548\u7387\u4F4E\u4E86\u597D\u591A\uFF0C\u67EF\u5357\u5C31\u5F00\u59CB\u9010\u6E10\u653E\u5F03\u4EE3\u7EC3\u3002</p><p>\u90A3\u65F6\u7684\u79BB\u5FC3\uFF0C\u5C31\u5F00\u59CB\u505A\u4E00\u4E9B\u4EE3\u5356\u548C\u4EE3\u4E70\uFF0C\u4E3B\u8981\u90FD\u662F\u8D70 8591 \u7684\u3002</p><h2 id="\u4E09\u3001\u67EF\u5357\u7684\u9996\u6B21\u9000\u8C37" tabindex="-1"><a class="header-anchor" href="#\u4E09\u3001\u67EF\u5357\u7684\u9996\u6B21\u9000\u8C37" aria-hidden="true">#</a> \u4E09\u3001\u67EF\u5357\u7684\u9996\u6B21\u9000\u8C37</h2><p>\u7531\u4E8E\u67EF\u5357\u665A\u4E0A\u7EC3\u53F7\u5230\u534A\u591C\uFF0C\u5F71\u54CD\u5230\u4E86\u7B2C\u4E8C\u5929\u4E0A\u73ED\uFF0C\u67EF\u5357\u5C31\u51B3\u5B9A\u4E0D\u518D\u4EE3\u7EC3\uFF0Clink \u88C5\u5907 \u548C \u67EF\u5357\u7684\u8D26\u53F7\u90FD\u7559\u7ED9\u4E86\u79BB\u5FC3\uFF08\u53F7\u4E0A\u6709\u8F6E\u70E7\uFF0C\u70E7\u662F\u4E00\u4EBA\u51FA\u4E00\u534A\u94B1\uFF09\uFF0C\u7136\u540E\u8BA9\u79BB\u5FC3\u518D\u627E\u4E2A\u4F19\u4F34\u4E00\u8D77\u505A\u4EE3\u7EC3\u3002</p><p>\u90A3\u65F6\u7684\u79BB\u5FC3\u662F\u627E\u4E86\u6211\u6765\u505A\u4F19\u4F34\u3002</p><p>\u4E4B\u540E\u67EF\u5357\u5728\u56DE\u5F52\u4E4B\u524D\u4FBF\u518D\u4E5F\u6CA1\u4E0A\u8FC7\u6E38\u620F\u3002</p><h2 id="\u56DB\u3001\u79BB\u5FC3\u5F0F\u6D17\u53F7-\u4E00\u5207\u4ECE\u7FA4\u4E3B\u8EAB\u4EFD\u5F00\u59CB" tabindex="-1"><a class="header-anchor" href="#\u56DB\u3001\u79BB\u5FC3\u5F0F\u6D17\u53F7-\u4E00\u5207\u4ECE\u7FA4\u4E3B\u8EAB\u4EFD\u5F00\u59CB" aria-hidden="true">#</a> \u56DB\u3001\u79BB\u5FC3\u5F0F\u6D17\u53F7\uFF0C\u4E00\u5207\u4ECE\u7FA4\u4E3B\u8EAB\u4EFD\u5F00\u59CB</h2><p>\u7531\u4E8E\u79BB\u5FC3\u662F\u7FA4\u4E3B\uFF0C\u8FD9\u5BFC\u81F4\u4E86\u5F88\u591A\u4EBA\u4F9D\u9644\u4ED6\uFF0C\u7ED9\u4E88\u4E86\u4ED6\u5F88\u591A\u4FBF\u5229\u3002</p><p>\u800C\u8FD9\uFF0C\u4E5F\u662F\u79BB\u5FC3\u5F0F\u6D17\u53F7\u7684\u5F00\u59CB\uFF0C\u4ED6\u628A\u5F88\u591A\u7FA4\u53CB\u7ED9\u4E88\u7684\u4FBF\u5229\uFF0C\u5F53\u6210\u81EA\u5DF1\u7684\uFF0C\u76F4\u63A5\u8D29\u5356\u53D8\u73B0\uFF0C\u6574\u4E2A\u8FC7\u7A0B\uFF0C\u539F\u4E3B\u662F\u4E00\u70B9\u90FD\u4E0D\u77E5\u60C5\u3002</p><p>\u5982\u679C\u4E0D\u662F\u67EF\u5357\u4E4B\u540E\u7684\u56DE\u5F52\uFF0C\u4E00\u5806\u7684\u4EBA\u88AB\u8499\u5728\u9F13\u91CC\u3002</p><h2 id="\u4E94\u3001\u67EF\u5357\u7684\u56DE\u5F52-\u8BA9\u4E8B\u4EF6\u5F00\u59CB\u5D2D\u9732\u5934\u89D2" tabindex="-1"><a class="header-anchor" href="#\u4E94\u3001\u67EF\u5357\u7684\u56DE\u5F52-\u8BA9\u4E8B\u4EF6\u5F00\u59CB\u5D2D\u9732\u5934\u89D2" aria-hidden="true">#</a> \u4E94\u3001\u67EF\u5357\u7684\u56DE\u5F52\uFF0C\u8BA9\u4E8B\u4EF6\u5F00\u59CB\u5D2D\u9732\u5934\u89D2</h2><p>2020\u5E74\u5E74\u521D\uFF0C\u7531\u4E8E\u75AB\u60C5\u539F\u56E0\uFF0C\u8FD9\u8BA9\u8BB8\u591A\u7684\u4EBA\u4EE5\u6E38\u620F\u6253\u53D1\u65F6\u95F4\uFF0C\u800C\u67EF\u5357\uFF0C\u4E5F\u5728 3\u6708\u4EFD \u56DE\u5F52\u67AB\u4E4B\u8C37\u3002</p><p>\u7136\u800C\uFF01\u8F6E\u548C\u70E7\uFF0C\u4EE5\u53CA link \u5957\uFF0C\u5168\u6CA1\u4E86\uFF0C\u4EC5\u5269\u4E0B\u4E00\u4E2A\u4EC5\u6709\u8054\u76DF\u7684 \u201C\u7A7A\u53F7\u201D \u800C\u5DF2\u3002</p><p>\u4E5F\u662F\u8FD9\u4E2A\u6708\uFF0C\u67EF\u5357\u5F00\u59CB\u4E86\u957F\u8FBE 9\u4E2A\u6708 \u7684\u88C5\u5907\u8BA8\u56DE\u4E4B\u8DEF\u3002</p><p>\u67EF\u5357\u4E00\u5B9A\u60F3\u4E0D\u5230\uFF0C\u5DF2\u7ECF 2021\u5E74\u7684\u827E\u6BD4\u6D3B\u52A8\u7ED3\u675F\u4E86\uFF0C\u4F9D\u65E7\u662F\u6CA1\u80FD\u8BA8\u56DE\u6240\u6709\u7684\u88C5\u5907\uFF08\u8FD9\u90FD\u662F\u540E\u8BDD\uFF09</p><h2 id="\u516D\u3001\u66F4\u591A\u4EBA\u7684\u88C5\u5907-\u5728\u539F\u4E3B\u4E0D\u77E5\u60C5\u7684\u60C5\u51B5\u4E0B-\u88AB\u79BB\u5FC3\u8D29\u5356" tabindex="-1"><a class="header-anchor" href="#\u516D\u3001\u66F4\u591A\u4EBA\u7684\u88C5\u5907-\u5728\u539F\u4E3B\u4E0D\u77E5\u60C5\u7684\u60C5\u51B5\u4E0B-\u88AB\u79BB\u5FC3\u8D29\u5356" aria-hidden="true">#</a> \u516D\u3001\u66F4\u591A\u4EBA\u7684\u88C5\u5907\uFF0C\u5728\u539F\u4E3B\u4E0D\u77E5\u60C5\u7684\u60C5\u51B5\u4E0B\uFF0C\u88AB\u79BB\u5FC3\u8D29\u5356</h2><p>\u7531\u4E8E\u79BB\u5FC3\u662F\u7FA4\u4E3B\u5173\u7CFB\uFF0C\u5DF2\u7ECF\u501F\u8FC7\u5F88\u591A\u4EBA\u7684 link \u5957\u3001\u8F6E\u56DE\u7B49\u9053\u5177\uFF0C\u5E76\u4E14\uFF0C\u90FD\u662F\u9690\u7792\u539F\u4E3B\uFF0C\u76F4\u63A5\u8D29\u5356\u3002</p><h2 id="\u4E03\u3001\u4ECE2020\u5E747\u6708\u5F00\u59CB-\u56F0\u96BE\u65F6\u671F\u7684\u79BB\u5FC3-\u6211\u7ED9\u4E86\u4ED6\u4E00\u4E9B\u5E2E\u52A9" tabindex="-1"><a class="header-anchor" href="#\u4E03\u3001\u4ECE2020\u5E747\u6708\u5F00\u59CB-\u56F0\u96BE\u65F6\u671F\u7684\u79BB\u5FC3-\u6211\u7ED9\u4E86\u4ED6\u4E00\u4E9B\u5E2E\u52A9" aria-hidden="true">#</a> \u4E03\u3001\u4ECE2020\u5E747\u6708\u5F00\u59CB\uFF0C\u56F0\u96BE\u65F6\u671F\u7684\u79BB\u5FC3\uFF0C\u6211\u7ED9\u4E86\u4ED6\u4E00\u4E9B\u5E2E\u52A9</h2><p>\u77E5\u9053\u79BB\u5FC3\u5DF2\u7ECF\u8D29\u5356\u4E86\u5F88\u591A\u4EBA\u7684\u88C5\u5907\uFF0C\u6025\u9700\u8981\u94B1\u8FDB\u884C\u507F\u8FD8\u7684\u4ED6\uFF0C\u5728\u4ED6\u6700\u9700\u8981\u7684\u65F6\u5019\uFF0C\u5B89\u6392\u4E86\u4E24\u4E2A\u6EE1\u7EA7\u7684\u53F7\u8FDB\u884C\u6392\u73ED\uFF0C\u518D\u600E\u4E48\u8BF4\uFF0C\u5F53\u65F6\u4E5F\u662F\u4ED6\u62C9\u6211\u6765\u4EE3\u7EC3\u7684\u3002</p><p>\u505A\u4EBA\u8981\u61C2\u5F97\u611F\u6069\u3002</p><p>\u7531\u4E8E\u6211\u6DF1\u523B\u77E5\u6653\u4EE3\u7EC3\u4E0D\u6613\uFF0C\u6240\u4EE5\uFF0C\u6240\u6709\u7684\u4EF7\u683C\u90FD\u662F\u6CA1\u6709\u8FDB\u884C\u62BD\u6210\u7684\uFF0C\u6BD4\u5982\uFF0C\u4E00\u5C0F\u65F6 35\u5143\uFF0C\u5C31\u80FD\u62FF\u5230\u624B\u6709 35\u5143\uFF08\u6211\u4E00\u76F4\u4EE5\u6765\u5FD7\u4E0D\u5728\u6B64\uFF0C\u800C\u662F\u60F3\u8BA9\u5C0F\u4F19\u4F34\u4EEC\u80FD\u62FF\u5230\u4E0D\u88AB\u62BD\u6210\u7684\u52B3\u52A8\u8D39\uFF09\u3002</p><p><a href="https://docs.qq.com/sheet/DTG9RU3NuTFd6Y0py?tab=BB08J2" target="_blank" rel="noopener noreferrer">\u70B9\u6211\u67E5\u770B\u6700\u540E\u4E00\u6B21\u5B89\u6392\u8868</a></p><h2 id="\u516B\u3001\u62C9\u9ED1\u79BB\u5FC3-\u4ECE\u4ED6\u6709\u94B1\u4E0D\u8FD8\u5F00\u59CB" tabindex="-1"><a class="header-anchor" href="#\u516B\u3001\u62C9\u9ED1\u79BB\u5FC3-\u4ECE\u4ED6\u6709\u94B1\u4E0D\u8FD8\u5F00\u59CB" aria-hidden="true">#</a> \u516B\u3001\u62C9\u9ED1\u79BB\u5FC3\uFF0C\u4ECE\u4ED6\u6709\u94B1\u4E0D\u8FD8\u5F00\u59CB</h2><p>\u5B89\u6392\u4E86\u4E24\u4E2A\u53F7\uFF0C\u4ECE7\u6708\u52309\u6708\uFF0C\u867D\u7136\u4E2D\u95F4\u6709\u65AD\u8FC7\u534A\u4E2A\u6708\uFF0C\u671F\u95F4\u4ED6\u8FD8\u81EA\u5DF1\u63A5\u4E86\u4E00\u4E9B\u5355\u7EC3\uFF0C\u600E\u4E48\u8BF4\u4E5F\u6709 2W \u8FDB\u8D26\u3002</p><p>\u7136\u800C\uFF0C\u4F9D\u65E7\u6B20\u7740\u8FD9\u4E9B\u88AB\u4ED6\u8D29\u5356\u88C5\u5907\u7684\u94B1\u3002</p><p>\u6211\u5BF9\u81EA\u5DF1\u62C9\u9ED1\u4ED6\u7684\u8BC4\u4EF7\u662F\uFF1A</p><p><strong>\u4ED6\u662F\u4E00\u4E2A\u4E0D\u61C2\u5F97\u611F\u6069\u7684\u4EBA\uFF0C\u4ED6\u7684\u6240\u4F5C\u6240\u4E3A\u8BA9\u6211\u5F88\u96BE\u8FC7\u3002</strong></p><h2 id="\u4E5D\u30012020\u5E7412\u6708-\u516C\u5F00\u4E86\u8FD9\u573A\u5B8C\u6574\u7684\u5403\u74DC\u4E8B\u4EF6" tabindex="-1"><a class="header-anchor" href="#\u4E5D\u30012020\u5E7412\u6708-\u516C\u5F00\u4E86\u8FD9\u573A\u5B8C\u6574\u7684\u5403\u74DC\u4E8B\u4EF6" aria-hidden="true">#</a> \u4E5D\u30012020\u5E7412\u6708\uFF0C\u516C\u5F00\u4E86\u8FD9\u573A\u5B8C\u6574\u7684\u5403\u74DC\u4E8B\u4EF6</h2><p>\u4E8B\u4EF6\u53D1\u751F\u4E4B\u540E\uFF0C\u65E0\u529F\u7684\u8D26\u53F7\u4E0A\u4F9D\u65E7\u7559\u4E0B\u4E24\u4EF6\u4F20\u8BF4\u6F06\u9ED1\u9970\u54C1\uFF0C\u5982\u679C\u662F\u76D7\u53F7\u7684\uFF0C\u4E0D\u53EF\u80FD\u4F1A\u7559\u4E0B\u8FD9\u4E24\u4EF6\u88C5\u5907\uFF0C\u56E0\u4E3A\u6839\u672C\u6CA1\u4E0A\u9501\uFF0C\u67AB\u4E86\u5C31\u80FD\u526A\u8D70\u3002</p><p>\u53EF\u80FD\u662F ta \u820D\u4E0D\u5F97\u4F20\u8BF4\u6F5C\u80FD\uFF0C\u6BC1\u4E86\u8FC7\u4E8E\u53EF\u60DC\u3002</p><p>\u4E5F\u8BB8\u662F\u5F53\u65F6\u6CA1\u6709\u627E\u5230\u5408\u9002\u7684\u4E70\u5BB6\uFF0C\u5982\u679C\u76F4\u63A5\u51FA\u4F20\u8BF4\u70C2\u6F5C\uFF0C\u7B49\u4E8E\u662F\u5168\u4E16\u754C\u90FD\u77E5\u9053 ta \u7684\u4E8B\u8FF9\u3002</p><p>\u611F\u5174\u8DA3\u53EF\u4EE5\u4E86\u89E3\u4E00\u4E0B\uFF1A<a href="https://pan.baidu.com/s/1Mog_VeGqyvfowlqEKS3GHA" target="_blank" rel="noopener noreferrer">\u767E\u5EA6\u76D8</a> \u63D0\u53D6\u7801: 5ves</p>',46);function h(d,n){return p}var t=a(r,[["render",h]]);export{t as default};
