import{_ as e,c as t}from"./app.78774e1d.js";const p={},a=t('<h1 id="\u89E3\u51B3\u65B0\u67AB\u4E4B\u8C37-\u53F0\u670D\u5192\u9669\u5C9B-\u804A\u5929\u6253\u5B57\u5168\u662F\u95EE\u53F7" tabindex="-1"><a class="header-anchor" href="#\u89E3\u51B3\u65B0\u67AB\u4E4B\u8C37-\u53F0\u670D\u5192\u9669\u5C9B-\u804A\u5929\u6253\u5B57\u5168\u662F\u95EE\u53F7" aria-hidden="true">#</a> \u89E3\u51B3\u65B0\u67AB\u4E4B\u8C37\uFF08\u53F0\u670D\u5192\u9669\u5C9B\uFF09\u804A\u5929\u6253\u5B57\u5168\u662F\u95EE\u53F7</h1><p>\u5F88\u591A\u840C\u65B0\u53EF\u80FD\u4F1A\u597D\u5947\uFF0C\u5728\u65B0\u67AB\u4E4B\u8C37\u6E38\u620F\u91CC\u6253\u5B57\u4F1A\u662F \u201C\uFF1F\u201D \u53F7\u663E\u793A\uFF0C\u8FD9\u662F\u7531\u4E8E\u53F0\u670D\u5192\u9669\u5C9B\u5BF9\u7B80\u4F53\u6587\u5B57\u652F\u6301\u4E0D\u53CB\u597D\u5BFC\u81F4\u7684\uFF0C\u8BF7\u5927\u5BB6\u5207\u6362\u7E41\u4F53\u8F93\u5165\u518D\u6765\u4EA4\u6D41\u54E6\uFF01</p><p><img src="https://fastly.jsdelivr.net/gh/tikas/cdn/img/blog/type/typing-problem.webp" alt="\u6234\u7C73\u5B89\u548C\u5E0C\u62C9\u7684\u5BF9\u8BDD"></p><div class="custom-container tip"><p class="custom-container-title">TIP</p><p>\u5982\u679C\u5207\u6362\u7E41\u4F53\u4F9D\u65E7\u8FD8\u662F\u6709\u95EE\u53F7\u663E\u793A\uFF0C\u60A8\u53EF\u4EE5\u4E0B\u8F7D\u4E00\u4E2A\u601D\u6E90\u9ED1\u4F53\u4F5C\u4E3A\u8F93\u5165\u6CD5\u7684\u9ED8\u8BA4\u663E\u793A\u5B57\u4F53\u3002</p><p><a href="https://github.com/adobe-fonts/source-han-sans/releases/latest" target="_blank" rel="noopener noreferrer">\u601D\u6E90\u9ED1\u4F53\u5B98\u65B9 Github \u4E0B\u8F7D\u9875\u9762</a></p><p>\u5982\u679C\u60A8\u7684\u7CFB\u7EDF\u662F windows 10+ \uFF0C\u60A8\u53EF\u4EE5\u5C1D\u8BD5\u4E0B\u8F7D OTC \u7248\u672C\uFF1A<code>Static Super OTC</code>\uFF08OTC \u7248\u672C\u5DF2\u7ECF\u62E5\u6709\u6240\u6709\u5B57\uFF09</p><p>\u5982\u679C OTC \u7248\u672C\u5E76\u4E0D\u80FD\u826F\u597D\u652F\u6301\u60A8\u7684\u7CFB\u7EDF\uFF08\u5982 win 7\uFF09\u548C\u8F93\u5165\u6CD5\uFF08\u67D0\u4E9B\u8F93\u5165\u6CD5\u4E0D\u652F\u6301\u663E\u793A\u5B57\u4F53\u66F4\u6539\u8BBE\u7F6E\uFF09\uFF0C\u8BF7\u4E0B\u8F7D OTF \u7248\u672C\uFF0C\u6700\u597D\u662F\u76F4\u63A5\u4E0B\u8F7D CN \u7248\u672C\u7684\uFF1A<code>Language Specific OTFs Simplified Chinese (\u7B80\u4F53\u4E2D\u6587)</code>\uFF08CN \u7248\u672C\u591A\u6570\u4F1A\u5305\u542B\u7E41\u4F53\uFF0C\u8FD9\u6837\u4E0D\u4F1A\u51FA\u73B0\u7F3A\u5B57\u73B0\u8C61\uFF0C\u5F53\u7136\uFF0C\u5982\u679C\u60A8\u89C9\u5F97\u8FD8\u662F\u4E0D\u591F\u4FDD\u5B88\uFF0C\u90A3\u5C31\u4E0B\u8F7D\u7E41\u4F53\u7684 OTF\uFF09</p></div><h2 id="\u{1F198}-\u66F4\u65B0-64-\u4F4D\u5E94\u7528\u7A0B\u5E8F\u540E\u6253\u5B57\u4E71\u7801" tabindex="-1"><a class="header-anchor" href="#\u{1F198}-\u66F4\u65B0-64-\u4F4D\u5E94\u7528\u7A0B\u5E8F\u540E\u6253\u5B57\u4E71\u7801" aria-hidden="true">#</a> \u{1F198} \u66F4\u65B0 64 \u4F4D\u5E94\u7528\u7A0B\u5E8F\u540E\u6253\u5B57\u4E71\u7801</h2><p>MapleSotry \u5168\u9762\u542F\u7528 64 \u4F4D\u5E94\u7528\u7A0B\u5E8F\uFF0C\u5982\u679C\u60A8\u6CA1\u6709\u4F7F\u7528\u7E41\u4F53\u533A\u57DF\u8BED\u8A00\uFF0C\u6B64\u65F6\uFF0C\u60A8\u662F\u4F7F\u7528\u8F6C\u533A\u5DE5\u5177\u6765\u542F\u52A8\u6E38\u620F\u7684\u3002</p><p>\u7531\u4E8E LE \u5E76\u4E0D\u652F\u6301 64 \u4F4D\u5E94\u7528\u7A0B\u5E8F\uFF0C\u73B0\u5728\u6240\u4F7F\u7528\u7684\u8F6C\u533A\u5DE5\u5177\u662F InWILL \u5F00\u53D1\u7684 Locale Remulator\u3002</p><p>\u5982\u679C\u60A8\u53EA\u662F\u6253\u5B57\u662F\u4E71\u7801\u663E\u793A\uFF0C\u90A3\u4E48\uFF0C\u60A8\u5E94\u8BE5\u628A\u7B2C\u4E09\u65B9\u7F24\u653E\u767B\u5F55\u5668\u66F4\u65B0\u5230 4.3 \u7248\u672C\u3002</p><p>\u5982\u679C\u60A8\u662F\u590D\u5236\u5185\u5BB9\u662F\u4E71\u7801\uFF0C\u90A3\u4E48\uFF0C\u8BF7\u7B49\u5F85 InWILL \u5F00\u53D1\u8005\u5BF9\u6B64\u95EE\u9898\u8FDB\u884C\u4FEE\u590D\u3002</p><h2 id="\u4E00\u3001\u62FC\u97F3\u5207\u6362\u7B80\u7E41\u4F53" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001\u62FC\u97F3\u5207\u6362\u7B80\u7E41\u4F53" aria-hidden="true">#</a> \u4E00\u3001\u62FC\u97F3\u5207\u6362\u7B80\u7E41\u4F53</h2><p>\u73B0\u5728\u4E3B\u6D41\u8F93\u5165\u6CD5\uFF0C\u62FC\u97F3\u662F\u7528\u6237\u91CF\u591A\uFF0C\u8FD9\u91CC\u53EA\u4EE5 \u201C\u641CG\u201D \u8F93\u5165\u6CD5\u6765\u4E3A\u4F8B\uFF0C\u53EA\u9700\u8981\u5728\u6E38\u620F\u91CC\u6309\u4EE5\u4E0B\u5FEB\u6377\u952E\u5C31\u53EF\u4EE5\u7B80\u7E41\u4F53\u5207\u6362\u5566\uFF1A</p><p><strong><code>Ctrl + Shift + F</code></strong></p><p>\u8FD9\u4E2A\u5FEB\u6377\u952E\u53EF\u4EE5\u5728\u8F93\u5165\u6CD5\u8BBE\u7F6E\u91CC\u4FEE\u6539\u7684\u54E6\uFF0C\u5176\u4ED6\u8F93\u5165\u6CD5\u8BF7\u81EA\u884C\u627E\u627E\u54C8\uFF0C\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://fastly.jsdelivr.net/gh/tikas/cdn/img/blog/type/pinyin-input-method-set-traditional-chinese.webp" alt="\u8F93\u5165\u6CD5\u7684\u7B80\u7E41\u4F53\u5FEB\u6377\u952E\u4FEE\u6539"></p><h2 id="\u4E8C\u3001\u4E94\u7B14\u8F93\u5165\u6CD5\u8BBE\u7F6E" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u3001\u4E94\u7B14\u8F93\u5165\u6CD5\u8BBE\u7F6E" aria-hidden="true">#</a> \u4E8C\u3001\u4E94\u7B14\u8F93\u5165\u6CD5\u8BBE\u7F6E</h2><p>\u7531\u4E8E\u5927\u591A\u4E94\u7B14\u5BF9 win10 \u7CFB\u7EDF\u652F\u6301\u4E0D\u592A\u597D\u53CB\uFF0C\u63A8\u8350\u4F7F\u7528 \u201C\u7CBE\u7075\u4E94\u7B14\u201D \uFF0C\u7531\u4E8E\u4ED6\u4EEC\u90FD\u662F\u76F8\u540C\u7684\u6E90\u7801\uFF08\u53CD\u6B63\u600E\u4E48\u770B\u90FD\u662F\u4E00\u6837\u7684\uFF0C\u5148\u6253\u4E0A\u4E00\u4E2A\u76F8\u540C\u6E90\u7801\u5427\uFF09\uFF0C\u5176\u8BBE\u7F6E\u65B9\u6CD5\u5E94\u8BE5\u662F\u4E00\u6837\u7684\uFF0C\u672C\u6587\u4EE5 \u201C\u82B1\u513F\u4E94\u7B14\u201D \u4E3A\u4F8B\u3002</p><blockquote><p>\u63A8\u8350\u5927\u5BB6\u4F7F\u7528 <code>\u7CBE\u7075\u4E94\u7B14</code>\uFF0C\u7531\u4E8E <code>\u82B1\u513F\u4E94\u7B14</code> \u65B0\u7248\u672C\u8FDB\u884C\u5927\u91CF\u66F4\u6539\uFF0C\u5BFC\u81F4\u975E\u5E38\u4E0D\u597D\u7528\uFF01</p></blockquote><p>\u6253\u5F00\u7CBE\u7075\u4E94\u7B14\u8BBE\u7F6E\u7A0B\u5E8F\uFF0C\u5728\u5E38\u7528\u8FD9\u91CC\uFF0C\u628A\u521D\u59CB\u72B6\u6001\u7684\u8F93\u5165\u5F00\u5173\u9009\u62E9 \u201C\u5173\u95ED\uFF08\u82F1\u6587\uFF09\u201D\uFF0C\u8FD9\u6837\u53EF\u4EE5\u628A\u5176\u4ED6\u8F93\u5165\u6CD5\u90FD\u53BB\u6389\uFF0C\u9ED8\u8BA4\u5C31\u53EA\u6709\u4E00\u4E2A\u4E94\u7B14\uFF0C\u540C\u65F6\uFF0C\u9ED8\u8BA4\u662F\u4E0D\u6253\u5B57\u7684\u82F1\u6587\u5B57\u6BCD\u8F93\u5165\uFF0C\u6211\u975E\u5E38\u559C\u6B22\u8FD9\u6837\u3002</p><p>\u5728\u9700\u8981\u6253\u5B57\u65F6\uFF0C\u53EA\u9700\u8981\u6309\u4E00\u4E0B \u5DE6\u8FB9\u7684 <code>Shift</code> \u952E\u5C31\u53EF\u4EE5\u4E86\u3002\u8BF7\u6CE8\u610F\uFF0C\u7531\u4E8E\u672C\u6587\u4E00\u4F1A\u8981\u628A\u53F3\u8FB9\u7684 Shift \u8BBE\u7F6E\u4E3A\u7E41\u4F53\u5207\u6362\uFF0C\u6240\u4EE5\uFF0C\u8BF7\u5927\u5BB6\u517B\u6210\u4EE5\u5DE6\u8FB9 <code>Shift</code> \u952E\u4E3A\u4E2D\u82F1\u6587\u5207\u6362\uFF0C\u5E38\u7528\u91CC\u8BBE\u7F6E\u5982\u4E0B\u56FE\uFF1A</p><p><img src="https://fastly.jsdelivr.net/gh/tikas/cdn/img/blog/type/common-settings.webp" alt="\u5E38\u7528\u91CC\u7684\u8BBE\u7F6E"></p><p>\u63A5\u4E0B\u6765\uFF0C\u5C31\u662F\u8BBE\u7F6E \u53F3\u8FB9 <code>Shift</code> \u952E\u4E3A\u7B80\u7E41\u4F53\u5207\u6362\u5566\u3002\u540C\u6837\u5728\u7CBE\u7075\u4E94\u7B14\u8BBE\u7F6E\uFF0C\u5728\u9AD8\u7EA7\u9009\u9879\uFF0C\u70B9\u51FB \u201C\u6309\u952E\u529F\u80FD\u201D \u6253\u5F00\u5FEB\u6377\u952E\u914D\u7F6E\u7A0B\u5E8F\uFF0C\uFF08\u8FD9\u91CC\u8981\u6CE8\u610F\uFF0C\u8BF7\u4E0D\u8981\u52FE\u9009\u524D\u9762\u7684 \u201C\u7981\u7528\u6309\u952E\u7EC4\u5408\u529F\u80FD\u5B9A\u4E49\u201D \uFF0C\u4E0D\u7136\u8BBE\u7F6E\u4F1A\u5931\u6548\uFF01\uFF09\uFF0C\u5982\u4E0B\u56FE\uFF1A</p><p><img src="https://fastly.jsdelivr.net/gh/tikas/cdn/img/blog/type/settings-in-advanced.webp" alt="\u9AD8\u7EA7\u91CC\u7684\u8BBE\u7F6E"></p><p><img src="https://fastly.jsdelivr.net/gh/tikas/cdn/img/blog/type/press-shortcuts-to-change-settings.webp" alt="\u6309\u952E\u5FEB\u6377\u952E\u66F4\u6539\u8BBE\u7F6E"></p><p>\u5728\u6309\u952E\u914D\u7F6E\u7A0B\u5E8F\u91CC\u7684\u53F3\u8FB9 <code>Shift</code> \u952E\uFF0C\u5F39\u51FA\u7684\u83DC\u5355\u91CC\u9009\u62E9\uFF1A\u6269\u5C55\u529F\u80FD-&gt;\u7B80\u4F53\u7801\u8868\u8F6C\u7E41\u4F53 \uFF0C\u5B8C\u6210\u8BBE\u7F6E\u4FDD\u5B58\u4E4B\u540E\uFF0C\u5230\u6B64\uFF0C\u5C31\u8BBE\u7F6E\u5B8C\u6210\u5566\uFF01</p><p>\u9ED8\u8BA4\u7A0B\u5E8F\u4E3A\u6253\u82F1\u6587\uFF0C\u8981\u6253\u4E2D\u6587\u65F6\uFF0C\u6309\u4E00\u4E0B\u5DE6\u8FB9\u7684 <code>Shift</code>\uFF0C\u5C31\u662F\u4E2D\u6587\u4E86\uFF0C\u5982\u679C\u9700\u8981\u6253\u7E41\u4F53\uFF0C\u518D\u6309\u4E00\u4E0B\u53F3\u8FB9\u7684 <code>Shift</code> \u952E\uFF0C\u5C31\u662F\u7E41\u4F53\u8F93\u51FA\u5566\uFF0C\u4E0D\u60F3\u7E41\u4F53\u65F6\uFF0C\u518D\u6309\u4E00\u4E0B \u53F3\u8FB9\u7684<code>Shift</code>\uFF0C\u5C31\u662F\u7B80\u4F53\u5566\uFF0C\u975E\u5E38\u65B9\u4FBF\uFF0C\u53EA\u9700\u8981\u6309\u4E00\u4E2A\u952E\u5C31\u53EF\u4EE5\u641E\u5B9A\uFF01</p><h2 id="\u4E09\u3001\u8865\u5145\u76F8\u5173" tabindex="-1"><a class="header-anchor" href="#\u4E09\u3001\u8865\u5145\u76F8\u5173" aria-hidden="true">#</a> \u4E09\u3001\u8865\u5145\u76F8\u5173</h2><p>\u5982\u679C\u4F60\u5728\u6E38\u620F\u91CC\u60F3\u6253\uFF1A\u7232\u4EC0\u9EBC\uFF1F\uFF0C\u4F1A\u53D1\u73B0\u7E41\u4F53\u7684 \u201C\u7232\u201D \u5B57\u663E\u793A\u662F\u95EE\u53F7\uFF0C\u4F60\u9700\u8981\u4E3A\u8FD9\u4E2A\u7E41\u4F53\u7684 \u201C\u7232\u201D \u5B57\u8BBE\u7F6E\u4E00\u4E0B\u81EA\u5B9A\u4E49\u77ED\u8BED\uFF0C\u8FD9\u6837\u4F1A\u597D\u4E9B\uFF0C\u6B63\u786E\u80FD\u663E\u793A\u7684\u4E3A\u4EC0\u4E48\u662F\u8FD9\u6837\u7684\uFF1A</p><p>\u7B80\u4F53\uFF1A\u4E3A\u4EC0\u4E48\uFF1F \u7E41\u4F53\uFF1A\u7232\u4EC0\u9EBC\uFF1F\uFF08\u6E38\u620F\u91CC\u663E\u793A\uFF1A\uFF1F\u4EC0\u9EBC\uFF09 \u6B63\u786E\u7E41\u4F53\uFF1A\u70BA\u4EC0\u9EBC\uFF1F\uFF08\u6E38\u620F\u91CC\u663E\u793A\uFF1A\u70BA\u4EC0\u9EBC\uFF09</p><p>\u201C\u70BA\u201D \u7684\u4E94\u7B14\u6253\u6CD5\uFF1Aetno \u6216\u8005 ytno</p><p>\u8FD8\u6709\u4E00\u4E9B\u5E38\u89C1\u8BCD\uFF0C\u6BD4\u5982\u505A\u7740\u4EC0\u4E48\u4E8B\u7684 \u201C\u7740\u201D \u4E5F\u662F\u663E\u793A\u4E0D\u51FA\u6765\u7684\uFF0C\u5176\u5B9E\u662F\u8981\u6253\u6210\uFF1A\u201C\u8457\u201D \u5C31\u53EF\u4EE5\u4E86\uFF0C\u66F4\u591A\u7684\u81EA\u5DF1\u7F51\u4E0A\u627E\u627E\u5427\uFF0C\u8FD9\u91CC\u5C31\u4E0D\u518D\u8865\u5145\u5566~</p><h2 id="\u56DB\u3001\u76F8\u5173\u94FE\u63A5" tabindex="-1"><a class="header-anchor" href="#\u56DB\u3001\u76F8\u5173\u94FE\u63A5" aria-hidden="true">#</a> \u56DB\u3001\u76F8\u5173\u94FE\u63A5</h2><p>\u641CG\u62FC\u97F3\u8F93\u5165\u6CD5\uFF1A<s>\uFF08\u94FE\u63A5\u4E0D\u89C1\u4E86\uFF09</s></p><p>\u7CBE\u7075\u4E94\u7B14\u8F93\u5165\u6CD5\uFF1A<a href="http://www.jlwubi.com/" target="_blank" rel="noopener noreferrer">\u7CBE\u7075\u4E94\u7B14</a></p><blockquote><p><strong>\u8B66\u544A\uFF1A\u8BF7\u4E0B\u8F7D v4.1.0.15 (2017/03/12)\u7248\u672C\u7684\u8F93\u5165\u6CD5\uFF0C\u7CBE\u7075\u4E94\u7B14\u540E\u7EED\u66F4\u65B0\u4F20\u8A00\u5305\u542B\u6728\u9A6C\uFF01\uFF01\uFF01\u6B64\u4FE1\u606F\u7531\u4E8E\u7F3A\u5C11\u76F8\u5173\u6587\u4EF6\u53BB\u8BC1\u5B9E\uFF08\u5B98\u65B9\u76EE\u524D\u53EA\u80FD\u4E0B\u8F7D 2017 \u5E74\u7684\u7248\u672C\uFF0C\u6B64\u8B66\u544A\u5199\u4E8E\uFF1A2022\u5E7401\u670810\u65E5\uFF0C\u6B22\u8FCE\u5927\u5BB6\u63D0\u4F9B\u76F8\u5173\u8BC1\u636E\uFF09\uFF0C<code>\u5C0FT\u540C\u5B66</code>\u672A\u80FD\u8BC1\u5B9E\u4F20\u8A00</strong></p></blockquote><p>\u82B1\u513F\u4E94\u7B14\u8F93\u5165\u6CD5\uFF1A<s>\uFF08\u94FE\u63A5\u4E0D\u89C1\u4E86\uFF09</s></p>',35);function i(o,r){return a}var c=e(p,[["render",i],["__file","typing-problem.html.vue"]]);export{c as default};
